exports._varsData = {};

var Vars = new function() {
    // TODO: It would be great to make Vars associated with modules?
    // That way we can distinguish what settings belong to what part of a code library
    //
    // Also would be good to extend the Vars class for different evironmental settings

    /**
     * Stores a key, value pair to Vars object for later retrieval.
     * This is useful for settings type of data that is not needed to be stored in the databse.
     *
     * @param string key
     * @param mixed value
     */
    this.set = function(key, value) {
        exports._varsData[key] = value;
    };

    /**
     * Gets a value that is stored on the Vars object.
     * A defaultValue can optionally be passed in the event a value cannot be found for the key.
     *
     * @param  string key
     * @param  mixed defaultValue
     * @return mixed
     */
    this.get = function(key, defaultValue) {
        return exports._varsData[key] || defaultValue || null;
    };
};

exports.Vars = Vars;